/*Add Text with style coding and logic part would be done later */
import * as React from 'react';
import { Text, View, StyleSheet, Image, FlatList } from 'react-native';
import axios from 'axios';


export default class SpaceCraft extends React.Component {
      constructor(props) {
        super(props);
        this.state = {
            aircrafts:[]
        };
    }

    componentDidMount() {
        this.getData()
            try {
            setInterval(async () => {
                this.getData()
            }, 5000); } 
            catch (e) {
            console.log(e);
        }
    }
  getData(){
    axios.get("https://ll.thespacedevs.com/2.0.0/config/spacecraft/").then(response=>{
      this.setState({aircrafts: response.data.results})
      console.log(response.data.results)
    }).catch(
      error=>{
        console.log(error.message)
        })
  }
  renderItem =({item})=>{
    return(
      <View style={
        {borderWidth:1,
         justifyContent:'center',
         allignItems:'center',
          marginBottom:5,
          elevation:10}
          }>
          <Image 
          source={
            {uri: item.agency.image_url}}
             style={{width:"100%", height:200, marginTop:15, marginBottom:15, marginRight:10}}></Image>
             <Text style={{fontWeight: 'bold', fontSize:20}}>{item.name}</Text>
             <Text style={{color: '#696969'}}>{item.agency.name}</Text>
             <Text>DESCRIPTION</Text>
             <Text style={{color:'#A9A9A9', marginLeft:10, marginRight:10}}>{item.agency.description}</Text>
          </View>
    )
    }
  render(){ 
 if (Object.keys(this.state.aircrafts).length === 0) {
            return (
                <View
                    style={{
                        flex: 1,
                        justifyContent: "center",
                        alignItems: "center"
                    }}>
                    <Text>Loading</Text>
                </View>
            )
        } else {
    return (
   <View style={styles.container}>
<View style={{flex: 0.25}}><Text>Space Crafts</Text></View>
<View style={{flex: 0.75}}>
        <FlatList
                        keyExtractor={this.keyExtractor}
                        data={this.state.aircrafts}
                        renderItem={this.renderItem}
                    /></View>
   </View>
            
    );
  }
  }
}

const styles = StyleSheet.create({
 container:{
   flex:1,
   alignItems:'center',
   justifyContent:'center'
 }
});
